binding.script:
var spd_mode = getprop("/flight-management/control/spd-mode");

if (spd_mode == "ias") {

	var ias = getprop("/flight-management/fcu-values/ias");

	if (350 > ias)
		setprop("/flight-management/fcu-values/ias", ias + 1);

} else {

	var mach = getprop("/flight-management/fcu-values/mach");

	if (0.82 > mach)
		setprop("/flight-management/fcu-values/mach", mach + 0.01);

}

var spd_mode = getprop("/flight-management/control/spd-mode");

if (spd_mode == "ias") {

	var ias = getprop("/flight-management/fcu-values/ias");

	if (ias > 0)
		setprop("/flight-management/fcu-values/ias", ias - 1);

} else {

	var mach = getprop("/flight-management/fcu-values/mach");

	if (mach > 0)
		setprop("/flight-management/fcu-values/mach", mach - 0.01);

}


var hdg_prop = "/flight-management/fcu-values/hdg";

if (359 > getprop(hdg_prop))
	setprop(hdg_prop, getprop(hdg_prop) + 1);
else
	setprop(hdg_prop, 0);


var hdg_prop = "/flight-management/fcu-values/hdg";

if (getprop(hdg_prop) > 0)
	setprop(hdg_prop, getprop(hdg_prop) - 1);
else
	setprop(hdg_prop, 359);


var step = getprop("/flight-management/control/alt-sel-mode");

var alt_prop = "/flight-management/fcu-values/alt";

if (41000 > getprop(alt_prop))
	setprop(alt_prop, getprop(alt_prop) + step);


var step = getprop("/flight-management/control/alt-sel-mode");

var alt_prop = "/flight-management/fcu-values/alt";

if (getprop(alt_prop) > 0)
	setprop(alt_prop, getprop(alt_prop) - step);


var sub_mode = getprop("/flight-management/control/ver-sub");

var vs_prop = "/flight-management/fcu-values/vs";

var fpa_prop = "/flight-management/fcu-values/fpa";

if (sub_mode == "vs") {

	if (3000 > getprop(vs_prop))
		setprop(vs_prop, getprop(vs_prop) + 100);

} else {

	if (15 > getprop(fpa_prop))
		setprop(fpa_prop, getprop(fpa_prop) + 1);

}


var sub_mode = getprop("/flight-management/control/ver-sub");

var vs_prop = "/flight-management/fcu-values/vs";

var fpa_prop = "/flight-management/fcu-values/fpa";

if (sub_mode == "vs") {

	if (getprop(vs_prop) > -2500)
		setprop(vs_prop, getprop(vs_prop) - 100);

} else {

	if (getprop(fpa_prop) > -10)
		setprop(fpa_prop, getprop(fpa_prop) - 1);

}


var radio = "/flight-management/freq/";

var ils_mode = getprop(radio~ "ils-mode");

if (ils_mode) {

	setprop(radio~ "ils-mode", 0);

	mcdu.rad_nav.switch_nav1(0);

} else {

	setprop(radio~ "ils-mode", 1);

	mcdu.rad_nav.switch_nav1(1);

}


var radio = "/flight-management/freq/";

var mls_mode = getprop(radio~ "mls-mode");

if (mls_mode) {

	setprop(radio~ "mls-mode", 0);

	mcdu.rad_nav.switch_nav2(0);

} else {

	setprop(radio~ "mls-mode", 1);

	mcdu.rad_nav.switch_nav2(1);

}

   var nd_disp = getprop("/instrumentation/efis/nd/display-mode");

   if (nd_disp == "ILS")
	  setprop("/instrumentation/nd/y-center", 0.5);
   elsif (nd_disp == "VOR")
	  setprop("/instrumentation/nd/y-center", 0.5);
   elsif (nd_disp == "NAV")
	  setprop("/instrumentation/nd/y-center", 0.5);
   elsif (nd_disp == "ARC")
	  setprop("/instrumentation/nd/y-center", 0.25);
   else
	  setprop("/instrumentation/nd/y-center", 0.5);

var nd_disp = getprop("/instrumentation/efis/nd/display-mode");

if (nd_disp == "ILS")
	setprop("/instrumentation/efis/nd/display-mode-num", 0);
elsif (nd_disp == "VOR")
	setprop("/instrumentation/efis/nd/display-mode-num", 1);
elsif (nd_disp == "NAV")
	setprop("/instrumentation/efis/nd/display-mode-num", 2);
elsif (nd_disp == "ARC")
	setprop("/instrumentation/efis/nd/display-mode-num", 3);
else
	setprop("/instrumentation/efis/nd/display-mode-num", 4);

var TrimVal = getprop("/controls/flight/rudder-trim");
setprop("/controls/flight/rudder-trim", TrimVal + 0.01);
setprop("/controls/switches/rudder-trim", 1);

var TrimVal = getprop("/controls/flight/rudder-trim");
setprop("/controls/flight/rudder-trim", TrimVal - 0.01);
setprop("/controls/switches/rudder-trim", -1);
