 			var first = getprop("/instrumentation/mcdu/f-pln/disp/first");

			var input = getprop("/instrumentation/mcdu/input");

			var comps = split('/', input);
			var spd = nil;

			var alt = nil;
			var ncomps = size(comps);
			if(ncomps == 1){
				alt = num(comps[0]);
			}
